@app.route("/")

def index():
    # Get country and odd
    randomer = random.choices(listofcountries1, weights=listofodds1)
    random_filtered = randomer[0]

    for i in range(len(listofcountries1)):
        if listofcountries1[i] == random_filtered:
            position = i
            the_odd = listofodds1[position]

    denominator = 1/the_odd
    denominator_rounded = round(denominator)

    # Get location
    # Use code for better results
    if listofcodes1[position] != "Unknown code":
        location = geolocator.geocode(random_filtered, country_codes=listofcodes1[position])
    else:
        location = geolocator.geocode(random_filtered)

    center_x = location.latitude
    center_y = location.longitude

    # Zoom to appropriate level
    zoom_level = listofzoom1[position]

    return render_template('index.html', variable=random_filtered, data=denominator_rounded, coord_x=center_x, coord_y=center_y, zoom_lvl=zoom_level)

if __name__ == "__main__":
    app.run()


